/**
 * Icon Component
 * 
 * Renders custom SVG icons without external dependencies.
 * 
 * @param {string} icon - Icon name from the icon library
 * @param {string} className - Additional CSS classes
 * @param {string} size - Size preset: 'xs', 'sm', 'md', 'lg', 'xl' or custom size
 */
const Icon = ({
  icon,
  className = '',
  size = 'md',
  variant = 'outline', // 'outline' | 'solid' - currently always renders outline
  ...props
}) => {
  // Size mapping
  const sizeClasses = {
    'xs': 'w-3 h-3',
    'sm': 'w-4 h-4',
    'md': 'w-5 h-5',
    'lg': 'w-6 h-6',
    'xl': 'w-8 h-8',
    '2xl': 'w-10 h-10',
  };

  const sizeClass = sizeClasses[size] || sizeClasses['md'];

  // Always use outline path (solid variant support disabled for now)
  const path = iconPaths[icon];

  if (!path) {
    console.warn(`Icon "${icon}" not found in icon library`);
    return null;
  }

  // Always render as outline
  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth={2}
      strokeLinecap="round"
      strokeLinejoin="round"
      className={`inline-block flex-shrink-0 ${sizeClass} ${className}`}
      aria-hidden="true"
      {...props}
    >
      <path d={path} />
    </svg>
  );
};

Icon.propTypes = {
  icon: PropTypes.string.isRequired,
  className: PropTypes.string,
  size: PropTypes.oneOf(['xs', 'sm', 'md', 'lg', 'xl', '2xl']),
  variant: PropTypes.oneOf(['outline', 'solid']),
};

